    private void jbComprarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbComprarActionPerformed
        double valor = definirPreco();
        jtfValor.setText(valor + "");
        int quantidade = Integer.parseInt(jsQuantidade.getValue().toString());
        valorTotal = (valor * quantidade) + valorTotal;
        


    }//GEN-LAST:event_jbComprarActionPerformed
    private int definirPreco() {
        String produto = jcbProduto.getSelectedItem().toString();
        if (produto.equals("Mouse")) {
            return 120;
        }
        if (produto.equals("Notebook")) {
            return 3500;
        }
        if (produto.equals("Impressora")) {
            return 570;
        }
        if (produto.equals("SSD 256GB")) {
            return 890;
        }
        if (produto.equals("Fone de Ouvido")) {
            return 45;
        }

        return 0;
    }
    private void jcbProdutoActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jcbProdutoActionPerformed
        jtfValor.setText(definirPreco() + "");
    }//GEN-LAST:event_jcbProdutoActionPerformed

    private void jbFinalizarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbFinalizarActionPerformed
        JOptionPane.showMessageDialog(null, "O custo total da sua compra Ã©: R$" + valorTotal);
    }//GEN-LAST:event_jbFinalizarActionPerformed

